/**
 * Creating a sidebar enables you to:
 - create an ordered group of docs
 - render a sidebar for each doc of that group
 - provide next/previous navigation

 The sidebars can be generated from the filesystem, or explicitly defined here.

 Create as many sidebars as you want.
 */

const componentsCategories = {
  Basic: 'basic',
  Form: 'form',
  Overlays: 'overlays',
  Layout: 'layoutsAndTemplates',
  Lists: 'lists',
  Incubator: 'incubator'
};

module.exports = {
  // By default, Docusaurus generates a sidebar from the docs folder structure
  // tutorialSidebar: [{type: 'autogenerated', dirName: '.'}]

  // But you can create a sidebar manually
  tutorialSidebar: [
    {
      type: 'category',
      label: 'Getting Started',
      items: ['getting-started/setup', 'getting-started/usage', 'getting-started/v5', 'getting-started/v6']
    },
    {
      type: 'category',
      label: 'Foundation',
      items: [
        'foundation/style',
        'foundation/modifiers',
        'foundation/assets',
        'foundation/theme-manager',
        'foundation/colors'
      ]
    },
    {
      type: 'category',
      label: 'Components',
      items: ['Basic', 'Form', 'Overlays', 'Layout', 'Lists', 'Native', 'Incubator'].map(category => {
        return {
          type: 'category',
          label: category,
          items: [
            {
              type: 'autogenerated',
              dirName: `components/${componentsCategories[category]}`
            }
          ]
        };
      })
    }
  ]
};
